import React, { Component } from "react";
import axios from "axios";

import { ListGroup } from "react-bootstrap";

export default class UserInfo extends Component {
  state = {
    userInfo: []
  };

  componentDidMount = async () => {
    const url = "https://jsonplaceholder.typicode.com";
    const {
      match: { params }
    } = this.props;

    const userInfo = await axios
      .get(`${url}/users/${params.userId}`)
      .then(res => {
        return res.data;
      });
    this.setState({ userInfo }, () => {
      console.log("console =>", this.state.userInfo);
    });
  };

  render() {
    const { userInfo } = this.state;
    return (
      <div>
        <div>
          <ListGroup variant="flush">
            <ListGroup.Item>ID: {userInfo.id}</ListGroup.Item>
            <ListGroup.Item>NAME: {userInfo.name}</ListGroup.Item>
            <ListGroup.Item>USERNAME: {userInfo.username}</ListGroup.Item>
            <ListGroup.Item>PHONE: {userInfo.phone}</ListGroup.Item>
            <ListGroup.Item>SITE: {userInfo.website}</ListGroup.Item>
          </ListGroup>
        </div>
      </div>
    );
  }
}
