import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

// import { Container } from './styles';

export default class Users extends Component {
  state = {
    userInfo: []
  };

  handleUserInfo = async id => {
    console.log("entro");
    const url = "https://jsonplaceholder.typicode.com";

    const userInfo = await axios.get(`${url}/users/${id}`).then(res => {
      return res.data;
    });

    this.setState({ userInfo }, () => {
      console.log("console =>", this.state.userInfo);
    });
  };

  render() {
    return (
      <div>
        {this.props.posts.map((user, index) => (
          <li>
            {console.log("return =>", index)}
            {user.name}
            <Button onClick={() => this.handleUserInfo(user.id)}>
              mostrar comments
            </Button>
            <div>{this.state.userInfo.id}</div>
          </li>
        ))}
      </div>
    );
  }
}
